<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Tagihanpdf extends CI_Controller {     function __construct()    {        parent::__construct();        $this->load->library("Pdf");    }	    public function create_pdf() {    //============================================================+    // File name   : example_001.php    // Begin       : 2008-03-04    // Last Update : 2013-05-14    //    // Description : Example 001 for TCPDF class    //               Default Header and Footer    //    // Author: Nicola Asuni    //    // (c) Copyright:    //               Nicola Asuni    //               Tecnick.com LTD    //               www.tecnick.com    //               info@tecnick.com    //============================================================+     /**    * Creates an example PDF TEST document using TCPDF    * @package com.tecnick.tcpdf    * @abstract TCPDF - Example: Default Header and Footer    * @author Nicola Asuni    * @since 2008-03-04    */		    // create new PDF document    $pdf = new TCPDF(PDF_PAGE_ORIENTATION, PDF_UNIT, PDF_PAGE_FORMAT, true, 'UTF-8', false);	     // set document information    $pdf->SetCreator(PDF_CREATOR);    $pdf->SetAuthor('Charles');    $pdf->SetTitle('Tagihan ID Card');    $pdf->SetSubject('Tagihan ID Card');    $pdf->SetKeywords('Tagihan, ID Card, Invoice');        // set default header data    //$pdf->SetHeaderData(PDF_HEADER_LOGO, PDF_HEADER_LOGO_WIDTH, PDF_HEADER_TITLE, PDF_HEADER_STRING, array(0,0,0), array(0,64,128));	$pdf->SetHeaderData(PDF_HEADER_LOGO, PDF_HEADER_LOGO_WIDTH, '', '', array(0,0,0), array(0,0,0));    $pdf->setFooterData(array(0,0,0), array(0,0,0));      // set header and footer fonts    $pdf->setHeaderFont(Array(PDF_FONT_NAME_MAIN, '', PDF_FONT_SIZE_MAIN));    $pdf->setFooterFont(Array(PDF_FONT_NAME_DATA, '', PDF_FONT_SIZE_DATA));       // set default monospaced font    $pdf->SetDefaultMonospacedFont(PDF_FONT_MONOSPACED);      // set margins    $pdf->SetMargins(PDF_MARGIN_LEFT, PDF_MARGIN_TOP, PDF_MARGIN_RIGHT);    $pdf->SetHeaderMargin(PDF_MARGIN_HEADER);    $pdf->SetFooterMargin(PDF_MARGIN_FOOTER);         // set auto page breaks    $pdf->SetAutoPageBreak(TRUE, PDF_MARGIN_BOTTOM);      // set image scale factor    $pdf->setImageScale(PDF_IMAGE_SCALE_RATIO);       // set some language-dependent strings (optional)    if (@file_exists(dirname(__FILE__).'/lang/eng.php')) {        require_once(dirname(__FILE__).'/lang/eng.php');        $pdf->setLanguageArray($l);    }        // ---------------------------------------------------------         // set default font subsetting mode    $pdf->setFontSubsetting(true);        // Set font    // dejavusans is a UTF-8 Unicode font, if you only need to    // print standard ASCII chars, you can use core fonts like    // helvetica or times to reduce file size.    $pdf->SetFont('dejavusans', '', 8, '', true);        // Add a page    // This method has several options, check the source code documentation for more information.    $pdf->AddPage();      // set text shadow effect    $pdf->setTextShadow(array('enabled'=>true, 'depth_w'=>0.2, 'depth_h'=>0.2, 'color'=>array(196,196,196), 'opacity'=>1, 'blend_mode'=>'Normal'));    	$kepada = $this->session->userdata('nama_persadmin');	$rp = $this->session->userdata('rp');	$terbilang = $this->session->userdata('tagihan_terbilang').' rupiah ';	$tanggal = $this->session->userdata('tanggal');	$kartu = $this->session->userdata('tagihan_kartu');	$biayaperkartu = $this->session->userdata('tagihan_biayaperkartu');		$tgl1 = date("d F Y");	$date2 = date("d", strtotime($tgl1."+7 day"));	$month2 = date("m", strtotime($tgl1."+7 day"));	$year2 = date("Y", strtotime($tgl1."+7 day"));	$this->load->helper('pembilang');	$month2_id = pembilang_bulan_id($month2);	$tgl2 = $date2.' '.$month2_id.' '.$year2;	    // Set some content to print    $html = <<<EOD    <p style="text-align:right;font-size:8;">CORPORATE HUMAN RESOURCES <br />	KOMPAS GRAMEDIA Building Unit 3, lt. 5<br />	Jl. Palmerah Selatan No.22-28. Jakarta 10270<br />	Telp. 5483008, 5480888, 5490666 Ext. 7518 Fax 5360672</p>		<h1 style="text-align:center;font-size:12;">NOTA TAGIHAN</h1>	<table style="text-align:left;font-size:9;" >		<tr>			<td style="width:15%;">Dari</td>			<td style="width:5%;">:</td>			<td style="width:80%;">Corporate Human Resources</td>		</tr>		<tr>			<td>Kepada</td>			<td>:</td>			<td>$kepada</td>		</tr>	</table>	<br /><br />		<table style="text-align:left;font-size:9;" >		<tr>			<td style="width:33%;">Sudah terima uang sebanyak</td>			<td style="width:4%;">:</td>			<td style="width:63%;">Rp $rp ,- ( Tunai / KM )</td>		</tr>		<tr>			<td>Terbilang</td>			<td>:</td>			<td> # $terbilang # </td>		</tr>		<tr>			<td>Untuk pembayaran</td>			<td>:</td>			<td>Pembuatan ID Card tanggal $tanggal</td>		</tr>	</table>	<br /><br /><br />	<table style="font-size:9;text-align:center;" border="1">		<tr>			<th>Bagian / Unit</th>			<th>Jumlah Kartu</th>			<th>Biaya Per-Kartu</th>			<th>Total</th>		</tr>		<tr>			<td>$kepada</td>			<td>$kartu</td>			<td>$biayaperkartu</td>			<td>$rp</td>		</tr>	</table>	<br /><br />		<table style="text-align:center;font-size:9;" >		<tr>			<td style="width:33%;">Menyetujui,</td>			<td style="width:33%;"></td>			<td style="width:33%;">Jakarta, $tgl1</td>		</tr>		<tr>			<td></td>			<td></td>			<td></td>		</tr>		<tr>			<td></td>			<td></td>			<td></td>		</tr>		<tr>			<td></td>			<td></td>			<td></td>		</tr>		<tr>			<td></td>			<td></td>			<td>FX Endri H.</td>		</tr>		<tr>			<td>(________________________)</td>			<td></td>			<td>HRIS Manager</td>		</tr>	</table>		<p style="font-size:9;">Kembali paling lambat tanggal $tgl2</p>	<p style="font-size:8;">Ket : Apabila dalam waktu 7 (tujuh) hari sejak diterimanya Nota Tagihan ini belum dikembalikan ke CHR-HRIS Dept. maka salinan Nota Tagihan ini dianggap sah sebagai bukti tagihan</p>		EOD;     // Print text using writeHTMLCell()    $pdf->writeHTMLCell(0, 0, '', '', $html, 0, 1, 0, true, '', true);        // ---------------------------------------------------------         // Close and output PDF document    // This method has several options, check the source code documentation for more information.    $pdf->Output('Tagihan ID Card.pdf', 'I');         //============================================================+    // END OF FILE    //============================================================+    }} /* End of file c_test.php *//* Location: ./application/controllers/c_test.php */